function($) {
    "use strict";
    $("a.page-scroll").bind("click",
            function(o) {
                var a = $(this);
                $("html, body").stop().animate({
                            scrollTop: $(a.attr("href")).offset().top - 50
                        },
                        1250, "easeInOutExpo"),
                    o.preventDefault()
            }),
        $("body").scrollspy({
            target: ".navbar-fixed-top",
            offset: 51
        }),
        $(".navbar-collapse ul li a:not(.dropdown-toggle)").click(function() {
            $(".navbar-toggle:visible").click()
        }),
        $("#mainNav").affix({
            offset: {
                top: 100
            }
        })
}(jQuery);